import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

// ---------- API „Ç®„É≥„Éâ„Éù„Ç§„É≥„ÉàÁîüÊàê ----------
const BACKEND_URL = (process.env.REACT_APP_BACKEND_URL || "").replace(/\/+$/, "");
const API        = `${BACKEND_URL ? BACKEND_URL : ""}/api`;
const api        = axios.create({ baseURL: API });



const App = () => {
  const [reservations, setReservations] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingReservation, setEditingReservation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Form state
  const [formData, setFormData] = useState({
    bench_id: 'front',
    user_name: '',
    start_time: '',
    end_time: ''
  });

  // Load reservations for selected date with comprehensive error handling
  const loadReservations = async (retryCount = 0, maxRetries = 3) => {
    try {
      console.log(`‰∫àÁ¥ÑË™≠„ÅøËæº„ÅøÈñãÂßã (Ë©¶Ë°å ${retryCount + 1}/${maxRetries + 1}):`, selectedDate);
      setLoading(true);
      setError(''); // „Ç®„É©„Éº„Çí„ÇØ„É™„Ç¢

      // „É™„ÇØ„Ç®„Çπ„ÉàË®≠ÂÆö„ÅÆÊúÄÈÅ©Âåñ
      const requestConfig = {
        params: { date: selectedDate },
        timeout: 8000 + (retryCount * 2000), // ÊÆµÈöéÁöÑ„Å´„Çø„Ç§„É†„Ç¢„Ç¶„Éà„ÇíÂª∂Èï∑
        validateStatus: function (status) {
          return status >= 200 && status < 300; // „Éá„Éï„Ç©„É´„Éà
        },
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      };

      const response = await axios.get(`${API}/reservations`, requestConfig);

      // „É¨„Çπ„Éù„É≥„Çπ„Éá„Éº„Çø„ÅÆÊ§úË®º
      if (!Array.isArray(response.data)) {
        throw new Error('„Çµ„Éº„Éê„Éº„Åã„ÇâÁÑ°Âäπ„Å™„Éá„Éº„ÇøÂΩ¢Âºè„ÅåËøî„Åï„Çå„Åæ„Åó„Åü');
      }

      // „Éá„Éº„Çø„ÅÆÂ¶•ÂΩìÊÄß„ÉÅ„Çß„ÉÉ„ÇØ
      const validReservations = response.data.filter(reservation => {
        return reservation && 
               reservation.id && 
               reservation.bench_id && 
               reservation.user_name && 
               reservation.start_time && 
               reservation.end_time;
      });

      if (validReservations.length !== response.data.length) {
        console.warn(`ÁÑ°Âäπ„Å™‰∫àÁ¥Ñ„Éá„Éº„Çø„ÇíÈô§Â§ñ: ${response.data.length - validReservations.length}‰ª∂`);
      }

      console.log('‰∫àÁ¥Ñ„Éá„Éº„ÇøÂèñÂæóÊàêÂäü:', validReservations);
      setReservations(validReservations);

    } catch (err) {
      console.error(`‰∫àÁ¥ÑË™≠„ÅøËæº„Åø„Ç®„É©„Éº (Ë©¶Ë°å ${retryCount + 1}):`, err);

      // „Ç®„É©„ÉºÂàÜÈ°û„Å®Ëá™Âãï„É™„Éà„É©„Ç§Âà§ÂÆö
      const isRetryableError = (
        err.code === 'ECONNABORTED' || // „Çø„Ç§„É†„Ç¢„Ç¶„Éà
        err.code === 'NETWORK_ERROR' || // „Éç„ÉÉ„Éà„ÉØ„Éº„ÇØ„Ç®„É©„Éº
        err.code === 'ERR_NETWORK' ||   // „Éç„ÉÉ„Éà„ÉØ„Éº„ÇØ„Ç®„É©„Éº (axios)
        (err.response && err.response.status >= 500) || // „Çµ„Éº„Éê„Éº„Ç®„É©„Éº
        (err.response && err.response.status === 503) || // „Çµ„Éº„Éì„ÇπÂà©Áî®‰∏çÂèØ
        (err.response && err.response.status === 504)    // „Çø„Ç§„É†„Ç¢„Ç¶„Éà
      );

      const shouldRetry = retryCount < maxRetries && isRetryableError;

      if (shouldRetry) {
        const delay = Math.min(1000 * (2 ** retryCount), 8000); // ÊúÄÂ§ß8Áßí
        console.log(`${delay/1000}ÁßíÂæå„Å´Ëá™Âãï„É™„Éà„É©„Ç§„Åó„Åæ„Åô...`);
        setError(`Êé•Á∂ö‰∏≠„Åß„Åô... (${retryCount + 1}ÂõûÁõÆÂÜçË©¶Ë°å‰∏≠)`);

        setTimeout(() => {
          loadReservations(retryCount + 1, maxRetries);
        }, delay);

      } else {
        // ÊúÄÂ§ß„É™„Éà„É©„Ç§ÂõûÊï∞„Å´ÈÅî„Åó„ÅüÂ†¥Âêà„Åæ„Åü„ÅØ„É™„Éà„É©„Ç§‰∏çÂèØ„Ç®„É©„Éº
        let errorMessage = '‰∫àÁ¥Ñ„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü';

        if (err.response) {
          // „Çµ„Éº„Éê„Éº„Ç®„É©„Éº„É¨„Çπ„Éù„É≥„Çπ„Åå„ÅÇ„ÇãÂ†¥Âêà
          errorMessage = err.response.data?.detail || 
                        `„Çµ„Éº„Éê„Éº„Ç®„É©„Éº (${err.response.status})`;
        } else if (err.code === 'ECONNABORTED' || err.code === 'ERR_NETWORK') {
          errorMessage = '„Éç„ÉÉ„Éà„ÉØ„Éº„ÇØÊé•Á∂ö„Å´ÂïèÈ°å„Åå„ÅÇ„Çä„Åæ„Åô';
        } else if (err.message) {
          errorMessage = err.message;
        }

        setError(errorMessage);
        console.error('ÊúÄÂ§ß„É™„Éà„É©„Ç§ÂõûÊï∞„Å´ÈÅî„Åó„Åæ„Åó„Åü:', err);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadReservations();
  }, [selectedDate]);

  // Create reservation
  const createReservation = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError('');

      const reservationData = {
        ...formData,
        start_time: `${selectedDate}T${formData.start_time}:00`,
        end_time: `${selectedDate}T${formData.end_time}:00`
      };

      await axios.post(`${API}/reservations`, reservationData);

      // Reset form and refresh
      setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });
      setShowCreateForm(false);
      await loadReservations();
    } catch (err) {
      setError(err.response?.data?.detail || '‰∫àÁ¥Ñ„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  // Update reservation
  const updateReservation = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError('');

      const updateData = {
        user_name: formData.user_name,
        start_time: `${selectedDate}T${formData.start_time}:00`,
        end_time: `${selectedDate}T${formData.end_time}:00`
      };

      await axios.put(`${API}/reservations/${editingReservation.id}`, updateData);

      // Reset form and refresh
      setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });
      setEditingReservation(null);
      await loadReservations();
    } catch (err) {
      setError(err.response?.data?.detail || '‰∫àÁ¥Ñ„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  // Start editing
  const startEdit = (reservation) => {
    const startTime = new Date(reservation.start_time).toTimeString().substring(0, 5);
    const endTime = new Date(reservation.end_time).toTimeString().substring(0, 5);

    setFormData({
      bench_id: reservation.bench_id,
      user_name: reservation.user_name,
      start_time: startTime,
      end_time: endTime
    });
    setEditingReservation(reservation);
    setShowCreateForm(false);
  };

  // Date navigation
  const changeDate = (days) => {
    const newDate = new Date(selectedDate);
    newDate.setDate(newDate.getDate() + days);
    setSelectedDate(newDate.toISOString().split('T')[0]);
  };

  // Format date for display
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ja-JP', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      weekday: 'long'
    });
  };

  // Generate time slots for timetable (every 30 minutes, 7:00-22:00)
  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 7; hour <= 22; hour++) {
      for (let minute = 0; minute < 60; minute += 30) {
        const timeString = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        slots.push(timeString);
      }
    }
    return slots;
  };

  // Calculate reservation bar properties
  const calculateBarProperties = (reservation) => {
    const startDate = new Date(reservation.start_time);
    const endDate = new Date(reservation.end_time);

    const startMinutes = startDate.getHours() * 60 + startDate.getMinutes();
    const endMinutes = endDate.getHours() * 60 + endDate.getMinutes();
    const durationMinutes = endMinutes - startMinutes;

    // 7:00 (420ÂàÜ) „ÇíÂü∫Ê∫ñÁÇπ„Å®„Åó„Å¶Ë™øÊï¥
    const baseOffsetMinutes = 7 * 60; // 7:00 = 420ÂàÜ
    const adjustedStartMinutes = startMinutes - baseOffsetMinutes;

    // Each 30-minute slot is 40px high
    const pixelsPerMinute = 40 / 30;
    const top = adjustedStartMinutes * pixelsPerMinute;
    const height = durationMinutes * pixelsPerMinute;

    return { top, height };
  };

  // Get reservation color
  const getReservationColor = (index) => {
    const colors = [
      '#667eea', '#764ba2', '#f093fb', '#f5576c',
      '#4facfe', '#00f2fe', '#43e97b', '#38f9d7',
      '#ffecd2', '#fcb69f', '#a8edea', '#fed6e3'
    ];
    return colors[index % colors.length];
  };

  // Auto cleanup old data function
  const cleanupOldData = async () => {
    try {
      setLoading(true);
      setError('');

      const response = await axios.post(`${API}/cleanup/old-data`, {}, {
        timeout: 15000 // 15Áßí„Çø„Ç§„É†„Ç¢„Ç¶„Éà
      });

      console.log('„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„ÉóÂÆå‰∫Ü:', response.data);

      // ÊàêÂäü„É°„ÉÉ„Çª„Éº„Ç∏„ÇíË°®Á§∫
      if (response.data.deleted_count > 0) {
        alert(`‚úÖ ${response.data.deleted_count}‰ª∂„ÅÆÂè§„ÅÑ‰∫àÁ¥Ñ„Éá„Éº„Çø„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü`);
      } else {
        alert('‚ÑπÔ∏è ÂâäÈô§ÂØæË±°„ÅÆ„Éá„Éº„Çø„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü');
      }

      // ‰∫àÁ¥Ñ„É™„Çπ„Éà„ÇíÊõ¥Êñ∞
      await loadReservations();

    } catch (err) {
      console.error('„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó„Ç®„É©„Éº:', err);
      const errorMessage = err.response?.data?.detail || '„Éá„Éº„Çø„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü';
      setError(`„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó„Ç®„É©„Éº: ${errorMessage}`);
    } finally {
      setLoading(false);
    }
  };

  const timeSlots = generateTimeSlots();

  return (
    <div className="app">
      <div className="container">
        <header className="header">
          <h1>„ÇØ„É™„Éº„É≥„Éô„É≥„ÉÅ‰∫àÁ¥Ñ„Ç∑„Çπ„ÉÜ„É†</h1>
          <p className="version-info">„Éê„Éº„Ç∏„Éß„É≥ 2.0 - ÊîπÂñÑÁâà</p>
        </header>

        {/* Date Navigation */}
        <div className="date-navigation">
          <button onClick={() => changeDate(-1)} className="nav-button">
            ‚Üê ÂâçÊó•
          </button>
          <div className="current-date">
            {formatDate(selectedDate)}
          </div>
          <button onClick={() => changeDate(1)} className="nav-button">
            ÁøåÊó• ‚Üí
          </button>
        </div>

        {/* Create Reservation Button & Manual Refresh & Cleanup */}
        <div className="action-buttons">
          <button 
            onClick={() => {
              setShowCreateForm(true);
              setEditingReservation(null);
              setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });
            }}
            className="create-button"
          >
            Êñ∞Ë¶è‰∫àÁ¥Ñ
          </button>

          <button 
            onClick={() => {
              console.log('ÊâãÂãïÊõ¥Êñ∞„Éú„Çø„É≥„Åå„ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åæ„Åó„Åü');
              loadReservations(0, 3); // ÊâãÂãïÊõ¥Êñ∞ÊôÇ„ÇÇËá™Âãï„É™„Éà„É©„Ç§Ê©üËÉΩ„Çí‰ΩøÁî®
            }}
            className="refresh-button"
            disabled={loading}
            title="‰∫àÁ¥Ñ‰∏ÄË¶ß„ÇíÊâãÂãï„ÅßÊõ¥Êñ∞"
          >
            {loading ? 'üîÑ Êõ¥Êñ∞‰∏≠...' : 'üîÑ Êõ¥Êñ∞'}
          </button>

          <button 
            onClick={() => {
              if (window.confirm('30Êó•„Çà„ÇäÂè§„ÅÑ‰∫àÁ¥Ñ„Éá„Éº„Çø„ÇíÂâäÈô§„Åó„Åæ„Åô„ÄÇ\n„Åì„ÅÆÊìç‰Ωú„ÅØÂèñ„ÇäÊ∂à„Åõ„Åæ„Åõ„Çì„ÄÇ\nÂÆüË°å„Åó„Åæ„Åô„ÅãÔºü')) {
                cleanupOldData();
              }
            }}
            className="cleanup-button"
            disabled={loading}
            title="30Êó•„Çà„ÇäÂè§„ÅÑ‰∫àÁ¥Ñ„Éá„Éº„Çø„ÇíÂâäÈô§„Åó„Å¶„Éá„Éº„Çø„Éô„Éº„ÇπÂÆπÈáè„ÇíÁØÄÁ¥Ñ"
          >
            {loading ? 'üßπ ÂâäÈô§‰∏≠...' : 'üßπ Âè§„ÅÑ„Éá„Éº„ÇøÂâäÈô§'}
          </button>
        </div>

        {/* Error Message with Retry Option */}
        {error && (
          <div className="error-container">
            <div className="error-message">
              ‚ö†Ô∏è {error}
            </div>
            <button 
              onClick={() => {
                setError('');
                loadReservations(0, 3);
              }}
              className="retry-button"
              disabled={loading}
            >
              {loading ? 'ÂÜçË©¶Ë°å‰∏≠...' : 'üîÑ ÂÜçË©¶Ë°å'}
            </button>
          </div>
        )}

        {/* Create/Edit Form */}
          {(showCreateForm || editingReservation) && (
          <div className="form-container">
            <h3>{editingReservation ? '‰∫àÁ¥ÑÁ∑®ÈõÜ' : 'Êñ∞Ë¶è‰∫àÁ¥Ñ'}</h3>
            <form onSubmit={editingReservation ? updateReservation : createReservation}>
              <div className="form-group">
                <label>„Éô„É≥„ÉÅ:</label>
                <select 
                  value={formData.bench_id} 
                  onChange={(e) => setFormData({...formData, bench_id: e.target.value})}
                  disabled={!!editingReservation}
                >
                  <option value="front">ÊâãÂâç</option>
                  <option value="back">Â••</option>
                </select>
              </div>

              <div className="form-group">
                <label>Âà©Áî®ËÄÖÂêç:</label>
                <input
                  type="text"
                  value={formData.user_name}
                  onChange={(e) => setFormData({...formData, user_name: e.target.value})}
                  required
                  placeholder="„ÅäÂêçÂâç„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ"
                />
              </div>

              <div className="form-group">
                <label>ÈñãÂßãÊôÇÂàª:</label>
                <input
                  type="time"
                  value={formData.start_time}
                  onChange={(e) => setFormData({...formData, start_time: e.target.value})}
                  min="07:00"
                  max="22:00"
                  step="1800"
                  required
                />
                <small style={{color: '#666', fontSize: '0.8rem'}}>
                  Âà©Áî®ÂèØËÉΩÊôÇÈñì: 7:00-22:00 (30ÂàÜÂàª„Åø)
                </small>
              </div>

              <div className="form-group">
                <label>ÁµÇ‰∫ÜÊôÇÂàª:</label>
                <input
                  type="time"
                  value={formData.end_time}
                  onChange={(e) => setFormData({...formData, end_time: e.target.value})}
                  min="07:00"
                  max="22:00"
                  step="1800"
                  required
                />
                <small style={{color: '#666', fontSize: '0.8rem'}}>
                  Âà©Áî®ÂèØËÉΩÊôÇÈñì: 7:00-22:00 (30ÂàÜÂàª„Åø)
                </small>
              </div>

              <div className="form-buttons">
                <button type="submit" disabled={loading}>
                  {editingReservation ? 'Êõ¥Êñ∞' : '‰∫àÁ¥Ñ‰ΩúÊàê'}
                </button>

                {editingReservation && (
                  <button 
                    type="button" 
                    className="delete-form-button"
                    disabled={loading}
                    onClick={() => {
                      console.log('ÂâäÈô§„Éú„Çø„É≥„Åå„ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åæ„Åó„Åü');
                      console.log('Á∑®ÈõÜ‰∏≠„ÅÆ‰∫àÁ¥Ñ:', editingReservation);

                      const handleDelete = async () => {
                        try {
                          // Ë©≥Á¥∞„Å™Á¢∫Ë™ç„ÉÄ„Ç§„Ç¢„É≠„Ç∞
                          const userConfirmed = window.confirm(
                            `‚ö†Ô∏è ‰∫àÁ¥Ñ„ÇíÂâäÈô§„Åó„Å¶„ÇÇ„Çà„Çç„Åó„ÅÑ„Åß„Åô„ÅãÔºü\n\n` +
                            `üìù Âà©Áî®ËÄÖ: ${editingReservation.user_name}\n` +
                            `‚è∞ ÊôÇÈñì: ${new Date(editingReservation.start_time).toTimeString().substring(0,5)} - ${new Date(editingReservation.end_time).toTimeString().substring(0,5)}\n` +
                            `üè¢ „Éô„É≥„ÉÅ: ${editingReservation.bench_id === 'front' ? 'ÊâãÂâç' : 'Â••'}\n\n` +
                            `‚ùå „Åì„ÅÆÊìç‰Ωú„ÅØÂèñ„ÇäÊ∂à„Åõ„Åæ„Åõ„Çì„ÄÇ\n\n` +
                            `ÂâäÈô§„Åô„ÇãÂ†¥Âêà„ÅØ„ÄåOK„Äç„ÇíÊäº„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ`
                          );

                          if (!userConfirmed) {
                            console.log('„É¶„Éº„Ç∂„Éº„ÅåÂâäÈô§„Çí„Ç≠„É£„É≥„Çª„É´„Åó„Åæ„Åó„Åü');
                            return;
                          }

                          console.log('ÂâäÈô§Âá¶ÁêÜ„ÇíÈñãÂßã„Åó„Åæ„Åô...');
                          console.log('ÂâäÈô§ÂØæË±°ID:', editingReservation.id);

                          setLoading(true);
                          setError('');

                          // DELETE „É™„ÇØ„Ç®„Çπ„ÉàÈÄÅ‰ø°
                          const response = await axios.delete(`${API}/reservations/${editingReservation.id}`);
                          console.log('‚úÖ ÂâäÈô§ÊàêÂäü:', response.data);

                          // Áä∂ÊÖã„Çí„É™„Çª„ÉÉ„Éà
                          setEditingReservation(null);
                          setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });

                          // ‰∫àÁ¥Ñ„É™„Çπ„Éà„ÇíÂÜçË™≠„ÅøËæº„Åø
                          await loadReservations();
                          console.log('‚úÖ ÂâäÈô§Âá¶ÁêÜ„ÅåÂÆå‰∫Ü„Åó„Åæ„Åó„Åü');

                        } catch (error) {
                          console.error('‚ùå ÂâäÈô§„Ç®„É©„Éº:', error);
                          if (error.response) {
                            console.error('„É¨„Çπ„Éù„É≥„Çπ„Ç®„É©„Éº:', error.response.data);
                            console.error('„Çπ„ÉÜ„Éº„Çø„Çπ„Ç≥„Éº„Éâ:', error.response.status);
                          }
