import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const App = () => {
  const [reservations, setReservations] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingReservation, setEditingReservation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Form state
  const [formData, setFormData] = useState({
    bench_id: 'front',
    user_name: '',
    start_time: '',
    end_time: ''
  });

  // Load reservations for selected date
  const loadReservations = async () => {
    try {
      console.log('‰∫àÁ¥ÑË™≠„ÅøËæº„ÅøÈñãÂßã:', selectedDate);
      setLoading(true);
      const response = await axios.get(`${API}/reservations`, {
        params: { date: selectedDate }
      });
      console.log('‰∫àÁ¥Ñ„Éá„Éº„ÇøÂèñÂæó:', response.data);
      setReservations(response.data);
    } catch (err) {
      console.error('‰∫àÁ¥ÑË™≠„ÅøËæº„Åø„Ç®„É©„Éº:', err);
      setError('‰∫àÁ¥Ñ„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadReservations();
  }, [selectedDate]);

  // Create reservation
  const createReservation = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError('');

      const reservationData = {
        ...formData,
        start_time: `${selectedDate}T${formData.start_time}:00`,
        end_time: `${selectedDate}T${formData.end_time}:00`
      };

      await axios.post(`${API}/reservations`, reservationData);

      // Reset form and refresh
      setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });
      setShowCreateForm(false);
      await loadReservations();
    } catch (err) {
      setError(err.response?.data?.detail || '‰∫àÁ¥Ñ„ÅÆ‰ΩúÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  // Update reservation
  const updateReservation = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError('');

      const updateData = {
        user_name: formData.user_name,
        start_time: `${selectedDate}T${formData.start_time}:00`,
        end_time: `${selectedDate}T${formData.end_time}:00`
      };

      await axios.put(`${API}/reservations/${editingReservation.id}`, updateData);

      // Reset form and refresh
      setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });
      setEditingReservation(null);
      await loadReservations();
    } catch (err) {
      setError(err.response?.data?.detail || '‰∫àÁ¥Ñ„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  // Start editing
  const startEdit = (reservation) => {
    const startTime = new Date(reservation.start_time).toTimeString().substring(0, 5);
    const endTime = new Date(reservation.end_time).toTimeString().substring(0, 5);

    setFormData({
      bench_id: reservation.bench_id,
      user_name: reservation.user_name,
      start_time: startTime,
      end_time: endTime
    });
    setEditingReservation(reservation);
    setShowCreateForm(false);
  };

  // Date navigation
  const changeDate = (days) => {
    const newDate = new Date(selectedDate);
    newDate.setDate(newDate.getDate() + days);
    setSelectedDate(newDate.toISOString().split('T')[0]);
  };

  // Format date for display
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ja-JP', { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      weekday: 'long'
    });
  };

  // Generate time slots for timetable (every 30 minutes, 7:00-22:00)
  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 7; hour <= 22; hour++) {
      for (let minute = 0; minute < 60; minute += 30) {
        const timeString = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        slots.push(timeString);
      }
    }
    return slots;
  };

  // Calculate reservation bar properties
  const calculateBarProperties = (reservation) => {
    const startDate = new Date(reservation.start_time);
    const endDate = new Date(reservation.end_time);

    const startMinutes = startDate.getHours() * 60 + startDate.getMinutes();
    const endMinutes = endDate.getHours() * 60 + endDate.getMinutes();
    const durationMinutes = endMinutes - startMinutes;

    // 7:00 (420ÂàÜ) „ÇíÂü∫Ê∫ñÁÇπ„Å®„Åó„Å¶Ë™øÊï¥
    const baseOffsetMinutes = 7 * 60; // 7:00 = 420ÂàÜ
    const adjustedStartMinutes = startMinutes - baseOffsetMinutes;

    // Each 30-minute slot is 40px high
    const pixelsPerMinute = 40 / 30;
    const top = adjustedStartMinutes * pixelsPerMinute;
    const height = durationMinutes * pixelsPerMinute;

    return { top, height };
  };

  // Get reservation color
  const getReservationColor = (index) => {
    const colors = [
      '#667eea', '#764ba2', '#f093fb', '#f5576c',
      '#4facfe', '#00f2fe', '#43e97b', '#38f9d7',
      '#ffecd2', '#fcb69f', '#a8edea', '#fed6e3'
    ];
    return colors[index % colors.length];
  };

  const timeSlots = generateTimeSlots();

  return (
    <div className="app">
      <div className="container">
        <header className="header">
          <h1>„ÇØ„É™„Éº„É≥„Éô„É≥„ÉÅ‰∫àÁ¥Ñ„Ç∑„Çπ„ÉÜ„É†</h1>
        </header>

        {/* Date Navigation */}
        <div className="date-navigation">
          <button onClick={() => changeDate(-1)} className="nav-button">
            ‚Üê ÂâçÊó•
          </button>
          <div className="current-date">
            {formatDate(selectedDate)}
          </div>
          <button onClick={() => changeDate(1)} className="nav-button">
            ÁøåÊó• ‚Üí
          </button>
        </div>

        {/* Create Reservation Button */}
        <div className="action-buttons">
          <button 
            onClick={() => {
              setShowCreateForm(true);
              setEditingReservation(null);
              setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });
            }}
            className="create-button"
          >
            Êñ∞Ë¶è‰∫àÁ¥Ñ
          </button>
        </div>

        {/* Error Message */}
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {/* Create/Edit Form */}
          {(showCreateForm || editingReservation) && (
          <div className="form-container">
            <h3>{editingReservation ? '‰∫àÁ¥ÑÁ∑®ÈõÜ' : 'Êñ∞Ë¶è‰∫àÁ¥Ñ'}</h3>
            <form onSubmit={editingReservation ? updateReservation : createReservation}>
              <div className="form-group">
                <label>„Éô„É≥„ÉÅ:</label>
                <select 
                  value={formData.bench_id} 
                  onChange={(e) => setFormData({...formData, bench_id: e.target.value})}
                  disabled={!!editingReservation}
                >
                  <option value="front">ÊâãÂâç</option>
                  <option value="back">Â••</option>
                </select>
              </div>

              <div className="form-group">
                <label>Âà©Áî®ËÄÖÂêç:</label>
                <input
                  type="text"
                  value={formData.user_name}
                  onChange={(e) => setFormData({...formData, user_name: e.target.value})}
                  required
                  placeholder="„ÅäÂêçÂâç„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ"
                />
              </div>

              <div className="form-group">
                <label>ÈñãÂßãÊôÇÂàª:</label>
                <input
                  type="time"
                  value={formData.start_time}
                  onChange={(e) => setFormData({...formData, start_time: e.target.value})}
                  min="07:00"
                  max="22:00"
                  step="1800"
                  required
                />
                <small style={{color: '#666', fontSize: '0.8rem'}}>
                  Âà©Áî®ÂèØËÉΩÊôÇÈñì: 7:00-22:00 (30ÂàÜÂàª„Åø)
                </small>
              </div>

              <div className="form-group">
                <label>ÁµÇ‰∫ÜÊôÇÂàª:</label>
                <input
                  type="time"
                  value={formData.end_time}
                  onChange={(e) => setFormData({...formData, end_time: e.target.value})}
                  min="07:00"
                  max="22:00"
                  step="1800"
                  required
                />
                <small style={{color: '#666', fontSize: '0.8rem'}}>
                  Âà©Áî®ÂèØËÉΩÊôÇÈñì: 7:00-22:00 (30ÂàÜÂàª„Åø)
                </small>
              </div>

              <div className="form-buttons">
                <button type="submit" disabled={loading}>
                  {editingReservation ? 'Êõ¥Êñ∞' : '‰∫àÁ¥Ñ‰ΩúÊàê'}
                </button>

                {editingReservation && (
                  <button 
                    type="button" 
                    className="delete-form-button"
                    disabled={loading}
                    onClick={() => {
                      console.log('ÂâäÈô§„Éú„Çø„É≥„Åå„ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åæ„Åó„Åü');
                      console.log('Á∑®ÈõÜ‰∏≠„ÅÆ‰∫àÁ¥Ñ:', editingReservation);

                      const handleDelete = async () => {
                        try {
                          // Ë©≥Á¥∞„Å™Á¢∫Ë™ç„ÉÄ„Ç§„Ç¢„É≠„Ç∞
                          const userConfirmed = window.confirm(
                            `‚ö†Ô∏è ‰∫àÁ¥Ñ„ÇíÂâäÈô§„Åó„Å¶„ÇÇ„Çà„Çç„Åó„ÅÑ„Åß„Åô„ÅãÔºü\n\n` +
                            `üìù Âà©Áî®ËÄÖ: ${editingReservation.user_name}\n` +
                            `‚è∞ ÊôÇÈñì: ${new Date(editingReservation.start_time).toTimeString().substring(0,5)} - ${new Date(editingReservation.end_time).toTimeString().substring(0,5)}\n` +
                            `üè¢ „Éô„É≥„ÉÅ: ${editingReservation.bench_id === 'front' ? 'ÊâãÂâç' : 'Â••'}\n\n` +
                            `‚ùå „Åì„ÅÆÊìç‰Ωú„ÅØÂèñ„ÇäÊ∂à„Åõ„Åæ„Åõ„Çì„ÄÇ\n\n` +
                            `ÂâäÈô§„Åô„ÇãÂ†¥Âêà„ÅØ„ÄåOK„Äç„ÇíÊäº„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ`
                          );

                          if (!userConfirmed) {
                            console.log('„É¶„Éº„Ç∂„Éº„ÅåÂâäÈô§„Çí„Ç≠„É£„É≥„Çª„É´„Åó„Åæ„Åó„Åü');
                            return;
                          }

                          console.log('ÂâäÈô§Âá¶ÁêÜ„ÇíÈñãÂßã„Åó„Åæ„Åô...');
                          console.log('ÂâäÈô§ÂØæË±°ID:', editingReservation.id);

                          setLoading(true);
                          setError('');

                          // DELETE „É™„ÇØ„Ç®„Çπ„ÉàÈÄÅ‰ø°
                          const response = await axios.delete(`${API}/reservations/${editingReservation.id}`);
                          console.log('‚úÖ ÂâäÈô§ÊàêÂäü:', response.data);

                          // Áä∂ÊÖã„Çí„É™„Çª„ÉÉ„Éà
                          setEditingReservation(null);
                          setFormData({ bench_id: 'front', user_name: '', start_time: '', end_time: '' });

                          // ‰∫àÁ¥Ñ„É™„Çπ„Éà„ÇíÂÜçË™≠„ÅøËæº„Åø
                          await loadReservations();
                          console.log('‚úÖ ÂâäÈô§Âá¶ÁêÜ„ÅåÂÆå‰∫Ü„Åó„Åæ„Åó„Åü');

                        } catch (error) {
                          console.error('‚ùå ÂâäÈô§„Ç®„É©„Éº:', error);
                          if (error.response) {
                            console.error('„É¨„Çπ„Éù„É≥„Çπ„Ç®„É©„Éº:', error.response.data);
                            console.error('„Çπ„ÉÜ„Éº„Çø„Çπ„Ç≥„Éº„Éâ:', error.response.status);
                          }
                          setError(`ÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ${error.response?.data?.detail || error.message}`);
                        } finally {
                          setLoading(false);
                        }
                      };

                      handleDelete();
                    }}
                  >
                    {loading ? 'ÂâäÈô§‰∏≠...' : 'üóëÔ∏è ‰∫àÁ¥ÑÂâäÈô§'}
                  </button>
                )}

                <button 
                  type="button" 
                  onClick={() => {
                    setShowCreateForm(false);
                    setEditingReservation(null);
                    setError('');
                  }}
                >
                  „Ç≠„É£„É≥„Çª„É´
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Timetable View */}
        <div className="timetable-container">
          {loading && <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>}

          <div className="timetable">
            {/* Header */}
            <div className="timetable-header">
              <div className="time-column-header">ÊôÇÂàª</div>
              <div className="bench-column-header">ÊâãÂâç</div>
              <div className="bench-column-header">Â••</div>
            </div>

            {/* Time grid */}
            <div className="timetable-grid">
              {/* Time column */}
              <div className="time-column">
                {timeSlots.map((time, index) => (
                  <div key={time} className="time-slot">
                    {time}
                  </div>
                ))}
              </div>

              {/* Front bench column */}
              <div className="bench-column" data-bench="front">
                <div className="bench-timeline">
                  {reservations
                    .filter(r => r.bench_id === 'front')
                    .map((reservation, index) => {
                      const { top, height } = calculateBarProperties(reservation);
                      return (
                        <div
                          key={reservation.id}
                          className="reservation-bar"
                          style={{
                            top: `${top}px`,
                            height: `${height}px`,
                            backgroundColor: getReservationColor(index),
                            left: '2px',
                            right: '2px'
                          }}
                          onClick={() => startEdit(reservation)}
                          title={`„ÇØ„É™„ÉÉ„ÇØ„Åó„Å¶Á∑®ÈõÜ: ${reservation.user_name} (${new Date(reservation.start_time).toTimeString().substring(0,5)} - ${new Date(reservation.end_time).toTimeString().substring(0,5)})`}
                        >
                          <div className="reservation-content">
                            <div className="reservation-user">{reservation.user_name}</div>
                            <div className="reservation-time">
                              {new Date(reservation.start_time).toTimeString().substring(0,5)} - {new Date(reservation.end_time).toTimeString().substring(0,5)}
                            </div>
                          </div>
                          <div className="edit-indicator">
                            Á∑®ÈõÜ
                          </div>
                        </div>
                      );
                    })}
                </div>
              </div>

              {/* Back bench column */}
              <div className="bench-column" data-bench="back">
                <div className="bench-timeline">
                  {reservations
                    .filter(r => r.bench_id === 'back')
                    .map((reservation, index) => {
                      const { top, height } = calculateBarProperties(reservation);
                      return (
                        <div
                          key={reservation.id}
                          className="reservation-bar"
                          style={{
                            top: `${top}px`,
                            height: `${height}px`,
                            backgroundColor: getReservationColor(index + 6),
                            left: '2px',
                            right: '2px'
                          }}
                          onClick={() => startEdit(reservation)}
                          title={`„ÇØ„É™„ÉÉ„ÇØ„Åó„Å¶Á∑®ÈõÜ: ${reservation.user_name} (${new Date(reservation.start_time).toTimeString().substring(0,5)} - ${new Date(reservation.end_time).toTimeString().substring(0,5)})`}
                        >
                          <div className="reservation-content">
                            <div className="reservation-user">{reservation.user_name}</div>
                            <div className="reservation-time">
                              {new Date(reservation.start_time).toTimeString().substring(0,5)} - {new Date(reservation.end_time).toTimeString().substring(0,5)}
                            </div>
                          </div>
                          <div className="edit-indicator">
                            Á∑®ÈõÜ
                          </div>
                        </div>
                      );
                    })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
